INSERT INTO `article_translations` (`id`, `title`, `description`, `content`, `source`, `item_id`, `language_id`, `created_at`, `updated_at`) VALUES
(118, 'Миграция 4-х ликвидированных банков на систему GNI «Мульти-Банк» в структуре Фонда Гарантирования Вкладов', NULL, '<p>Компания &laquo;GNI Software&raquo; совместно с Фондом Гарантирования Вкладов успешно реализовала проект по миграции данных из текущих систем 4 Банков в процессе ликвидации, по которым произошел страховой случай (апрель и май 2020 года).<br />В результате выполнения данного проекта, в системе GNI были рассчитаны необходимые компенсации гражданам Азербайджанской Республики в соответствии с требованиями текущего законодательства.<br /><br />В рамках проекта для выплаты компенсаций вкладчикам ликвидированных банков была также проведена онлайн интеграция системы GNI &laquo;Мульти-Банк&raquo; с АБС банков-агентов: ОАО &laquo;Kapital Bank&raquo; и ОАО &laquo;Международный Банк Азербайджан&raquo;.<br />Успешное исполнение указанного государственного проекта в кратчайшие сроки (1 месяц) позволило Фонду Гарантирования Вкладов оперативно исполнить свои обязательства перед вкладчиками и регулятором. Уже 1 июня 2020 года начались выплаты компенсаций в филиалах ОАО &laquo;Kapital Bank&raquo; и 15 июня 2020 года начались выплаты компенсаций в филиалах ОАО &laquo;Международный Банк Азербайджана&raquo;.<br />В скором времени планируется миграция всех активов и пассивов 4-х банков, находящихся в процессе ликвидации, в централизованную автоматизированную GNI &laquo;Мульти-Банк&raquo; систему.</p>', NULL, 46, 2, NULL, NULL),
(119, 'Migration of 4 liquidated banks to the GNI “Multi-Bank” system in the structure of the Deposit Guarantee Fund', NULL, '<p>GNI Software, together with the Deposit Guarantee Fund, successfully implemented a project of data migration from the current systems of 4 Banks in the liquidation process for which an insured event actually occurred (April and May 2020).<br />As a result of this project, the GNI system calculated the necessary compensation for citizens of the Republic of Azerbaijan in accordance with the requirements of current legislation.<br />Within the framework of the project, in order to pay compensation to depositors of liquidated banks, the GNI &ldquo;Multi-Bank&rdquo; system was also integrated online with ABS of agent banks: &ldquo;Kapital Bank&rdquo; OJSC and &ldquo;International Bank of Azerbaijan&rdquo; OJSC.<br /><br />Successful execution of the specified state project as soon as possible (1 month) allowed the Deposit Guarantee Fund to quickly fulfill its obligations to investors and the regulator. Already on June 1, 2020, compensation payments began in the branches of &ldquo;Kapital Bank&rdquo; OJSC and June 15, 2020 payments of compensation began in the branches of International &ldquo;Bank of Azerbaijan&rdquo; OJSC.<br /><br />In the near future, it is planned to migrate all the assets and liabilities of 4 banks in the process of liquidation into the centralized automated GNI &ldquo;Multi-Bank&rdquo; system.</p>', NULL, 46, 3, NULL, NULL),
(120, 'Переход ОАО «ДекаБанк» на систему GNI Software «Мульти-Банк» в структуре Фонда Гарантирования Вкладов', NULL, '<div data-redactor-tag=\"br\">Компания &laquo;GNI Software&raquo; в партнерстве с компанией &laquo;Smart Systems Technologies&raquo; успешно осуществила полный переход ОАО &laquo;ДекаБанк&raquo; с банковской системы &laquo;Прогресс&raquo; на централизованную банковскую систему &laquo;Мульти-Банк&raquo; Государственного Фонда Страхования Депозитов.&nbsp;</div>\n<div data-redactor-tag=\"br\">&nbsp;</div>\n<div data-redactor-tag=\"br\">В рамках указанного проекта была продолжена централизация управления Банками, находящимися в процессе ликвидации, через единую программную платформу.&nbsp;</div>\n<div data-redactor-tag=\"br\">&nbsp;</div>\n<div data-redactor-tag=\"br\">Данное решение позволяют существенно увеличить контроль за операциями и снизить операционные расходы Фонда.</div>', NULL, 47, 2, NULL, NULL),
(121, 'Migration of DecaBank OJSC to the GNI Software Multi-Bank System', NULL, '<p>GNI Software, in the partnership with Smart Systems Technologies, successfully completed the complete transition of DecaBank OJSC from the Progress banking system to the centralized Multi-Bank banking system of the State Deposit Insurance Fund.<br /><br />Within the framework of this project, the centralization of management of banks in the process of liquidation through a single software platform was continued. This solution allows to significantly increase control over operations and reduce the Fund&rsquo;s operating expenses.</p>', NULL, 47, 3, NULL, NULL);
